import React from "react";
import "../App.css"
import "../index.scss";

const Onerecepie = ({ data}) => {
  return (
    <div >
       { data.map((i)=>{
        return(
      <div className="receipe-content-area" key={i}>
        <div className="container">
          <div className="header">
          <img
              className="img"
              src={data.recepie_Image }
              alt="food"
            />         
          </div>
          <div className="text">
          <h1 className="food">{data.recepie_Name}</h1>
            <i> {data.Cook_Time}</i> <br />
            <i> {data.Prep_Serves} </i>

            <p className="info">{data.recepie_Description}</p>
          </div>
        </div>
      </div>
        )
       })}
    </div>
  );
};

export default Onerecepie;
